import jsTPS_Transaction from "../common/jsTPS.js"
/**
 * DeleteSong_Transaction
 * 
 * This class represents a transaction that works with adding a 
 * song. It will be managed by the transaction stack.
 * 
 */
export default class DeleteSong_Transaction extends jsTPS_Transaction {
    constructor(store) {
        super();
        this.store = store;
        this.deletionIndex = store.deleteIndex;
        this.deletedSong = store.songForDeletion;
    }

    doTransaction() {
        console.log("DELETE SONG TRANSACTION")
        this.store.deleteSongFromList();
        console.log("Store after call:");
        console.log(this.store.currentList);
        console.log(this.deletionIndex);
        console.log(this.songForDeletion);
    }

    undoTransaction() {
        console.log("UNDO DELETE SONG TRANSACTION")
        this.store.addNewSong();
        let songBody = songForDeletion;
        this.store.currentList.songs.push(songBody);
        console.log("store after adding back song ")
        console.log(this.store.currentList);

        console.log("Deletion Index:");
        console.log(this.deletionIndex);
        let index = this.store.currentList.songs.length-1;
        this.store.moveSong(this.deletionIndex, index);

    }
}
