import jsTPS_Transaction from "../common/jsTPS.js"
/**
 * EditSong_Transaction
 * 
 * This class represents a transaction that works with adding a 
 * song. It will be managed by the transaction stack.
 * 
 */
export default class EditSong_Transaction extends jsTPS_Transaction {
    constructor(store) {
        super();
        this.store = store;
        this.preEditSong = store.songForEdit;
        this.index = store.editIndex;
    }

    doTransaction() {
        console.log("EDIT SONG TRANSACTION");
        this.store.updateSongInList();
    }

    undoTransaction() {
        console.log("UNDO EDIT SONG TRANSACTION");
        this.store.changeSongById(this.index, this.preEditSong);
    }
}
