import jsTPS_Transaction from "../common/jsTPS.js"
import { useContext } from 'react'
import { GlobalStoreContext } from '../store'
/**
 * AddSong_Transaction
 * 
 * This class represents a transaction that works with adding a 
 * song. It will be managed by the transaction stack.
 * 
 */
export default class AddSong_Transaction extends jsTPS_Transaction {
    constructor() {
        super();
        const { store } = useContext(GlobalStoreContext);
    }

    doTransaction() {
        store.addSong();
    }

    undoTransaction() {
        let length = store.currentList.songs.length;
        store.setSongForDeletion(length-1);
        store.deleteSongFromList();
    }
}
